CREATE TABLE `category` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` VARCHAR(20) NOT NULL);

CREATE TABLE `products` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` VARCHAR(20) NOT NULL, `description` TEXT NOT NULL, `price` INTEGER NOT NULL, `picture` VARCHAR(64) NOT NULL, `category` INTEGER NOT NULL);

INSERT INTO `products` (`name`, `description`, `price`, `picture`, `category`) VALUES 
('CorelDRAW', "CorelDRAW Graphics Suite 2020 допоможе вам подолати будь-які бар'єри на шляху до функціональної креативності! У пакеті представлені професійні інструменти для редагування фотографій, розроблення векторної графіки, макетування сторінок і роботи з текстом — словом, все потрібне для створення та реалізації дизайн-проєктів практично на будь-якому пристрої.", 4345, 'coreldraw.jpg', 0)
, ('Paintstorm Studio', "Paintstorm studio Створена для професійного цифрового живопису. І головну роль у цій програмі грають кисті. Ми зробили все можливе, щоб ви могли легко та швидко відрегулювати пензлі, що відповідають вашому стилю малювання.", 5435, 'paintstorm-studio.jpg', 0)
, ('ToonBoom Harmony', "Harmony 20, провідне програмне забезпечення для 2D-анімації в галузі. Лінійки, напрямні, контроль кольорів та функції, а також інноваційні деформатори збагачують вашу анімацію, приносячи реальність та фантазію в межах міліметрів один від одного.", 34566, 'toonboom-harmony.jpeg', 0)
, ('Adobe Acrobat Pro', "Максимально спростіть робочий процес із найкращими у світі інструментами для роботи з PDF. Перетворюйте, редагуйте, підписуйте та встановлюйте захист документів PDF — усе це в одній зручній програмі.", 3545, 'adobe-acrobat-pro.jpg', 1)
, ('Microsoft Office', "Office Для дому та навчання 2019 — ідеальне рішення для тих, кому необхідний класичний набір застосунків для одного пристрою: ПК (з Windows 10) або Mac. Всі базові інструменти для роботи з документами, реалізації творчих ідей, виконання навчальних і домашніх проектів зібрані в цьому наборі. А з новими функціями у версії 2019 працювати із застосунками Office стало ще простіше.", 8758, 'microsoft-office.jpg', 1)
, ('Эксперт-Смета', "Программный комплекс «Эксперт-Смета» предназначен для формирования проектно-сметной и подрядной документации, расчета договорной цены, а также получения документов, связанных с учетом выполненных работ и списанием материалов.", 7655, 'expert-smeta.jpg', 1)
, ('Autodesk Inventor', "Програмне забезпечення Inventor® CAD забезпечує професійний 3D-механічний дизайн, документацію та засоби моделювання продуктів. Працюйте ефективно за допомогою потужної суміші параметрів, прямої, вільної форми та заснованих на правилах дизайнерських можливостей.", 56461, 'autodesk-inventor.jpg', 2)
, ('PTC Creo', "Фантазуйте, конструює, створюйте і проектують інноваційні вироби більш оптимально за допомогою програмного забезпечення і рішень Creo 3D CAD / CAM / CAE для проектування і розробки виробів.", 56546, 'ptc-creo.png', 2)
, ('КОМПАС 3D', "КОМПАС-3D - це російська система тривимірного проектування, що стала стандартом для тисяч підприємств і десятків тисяч професійних користувачів.", 47462, 'compas-3d.jpg', 2);